descriptor.controllerScript:
var requestTimeout = 500;
const commandStatusPollingInterval = 200;

const welcome = 'Welcome to ThingsBoard RPC remote shell.\n';

var terminal, rpcEnabled, simulated, deviceName, cwd;
var commandExecuting = false;

self.onInit = function() {
    var subscription = self.ctx.defaultSubscription;
    rpcEnabled = subscription.rpcEnabled;
    if (subscription.targetDeviceName && subscription.targetDeviceName.length) {
        deviceName = subscription.targetDeviceName;
    } else {
        deviceName = 'Simulated';
        simulated = true;
    }
    if (self.ctx.settings.requestTimeout) {
        requestTimeout = self.ctx.settings.requestTimeout;
    }

    terminal = $('#device-terminal', self.ctx.$container).terminal(
        function (command) {
            if (command && command.trim().length) {
                try {
                    if (simulated) {
                        this.echo(command);
                    } else {
                        sendCommand(this, command);
                    }
                } catch(e) {
                    this.error(new String(e));
                }
            } else {
               this.echo('');
            }
        }, {
            greetings: false,
            prompt: rpcEnabled ? currentPrompt : '',
            name: 'shell',
            pauseEvents: false,
            keydown: (e, term) => {
                if ((e.which == 67 || e.which == 68) && e.ctrlKey) { // CTRL+C || CTRL+D
                    if (commandExecuting) {
                        terminateCommand(term);
                        return false;
                    }
                }
            },
            onInit: initTerm
        }
    );
}

function initTerm(terminal) {
    terminal.echo(welcome);
    if (!rpcEnabled) {
        terminal.error('Target device is not set!\n');
    } else {
        terminal.echo(`Current target device for RPC terminal: [[b;#fff;]${deviceName}]\n`);
        if (!simulated) {
            terminal.pause();
            getTermInfo(terminal,
            (remoteTermInfo) => {
                if (remoteTermInfo) {
                    terminal.echo(`Remote platform info:`);
                    terminal.echo(`OS: [[b;#fff;]${remoteTermInfo.platform}]`);
                    if (remoteTermInfo.release) {
                        terminal.echo(`OS release: [[b;#fff;]${remoteTermInfo.release}]`);
                    }
                    terminal.echo('\r');
                } else {
                    terminal.echo('[[;#f00;]Unable to get remote platform info.\nDevice is not responding.]\n');
                }
                terminal.resume();
            });
        }
    }
}

function currentPrompt(callback) {
    if (cwd) {
        callback('[[b;#2196f3;]' + deviceName +']: [[b;#8bc34a;]' + cwd +']> ');
    } else {
        callback('[[b;#8bc34a;]' + deviceName +']> ');
    }
}

function getTermInfo(terminal, callback) {
    self.ctx.controlApi.sendTwoWayCommand('getTermInfo', null, requestTimeout).then(
        (termInfo) => {
            cwd = termInfo.cwd;
            if (callback) {
                callback(termInfo);
            } 
        },
        () => {
            if (callback) {
                callback(null);
            }
        }
    );
}

function sendCommand(terminal, command) {
    terminal.pause();
    var sendCommandRequest = {
        command: command,
        cwd: cwd
    };
    self.ctx.controlApi.sendTwoWayCommand('sendCommand', sendCommandRequest, requestTimeout).then(
        (responseBody) => {
            if (responseBody && responseBody.ok) {
                commandExecuting = true;
                setTimeout( pollCommandStatus.bind(null,terminal), commandStatusPollingInterval );
            } else {
                var error = responseBody ? responseBody.error : 'Unhandled error.';
                terminal.error(error);
                terminal.resume();
            }
        },
        () => {
            onRpcError(terminal);
        }
    );
}

function terminateCommand(terminal) {
    self.ctx.controlApi.sendTwoWayCommand('terminateCommand', null, requestTimeout).then(
        (responseBody) => {
            if (!responseBody.ok) {
                commandExecuting = false;
                terminal.error(responseBody.error);
                terminal.resume();
            } 
        },
        () => {
            onRpcError(terminal);
        }
    );    
}

function onRpcError(terminal) {
    var errorText = self.ctx.defaultSubscription.rpcErrorText;
    terminal.error(errorText);
    terminal.resume();
}

function pollCommandStatus(terminal) {
    self.ctx.controlApi.sendTwoWayCommand('getCommandStatus', null, requestTimeout).then(
        (commandStatusResponse) => {
            commandStatusResponse.data.forEach((dataElement) => {
                if (dataElement.stdout) {
                    terminal.echo(dataElement.stdout);
                }
                if (dataElement.stderr) {
                    terminal.error(dataElement.stderr);
                }
            });            
            if (commandStatusResponse.done) {
                commandExecuting = false;
                cwd = commandStatusResponse.cwd;
                terminal.resume();
            } else {
                var interval = commandStatusPollingInterval;
                if (!commandStatusResponse.data.length) {
                    interval *=5;
                }
                setTimeout( pollCommandStatus.bind(null,terminal), interval );
            }
        },
        () => {
            commandExecuting = false;
            onRpcError(terminal);
        }
    );
}

self.onResize = function () {
    if (terminal) {
        terminal.resize(self.ctx.width, self.ctx.height);
    }
}

self.onDestroy = function() {
}
